export default {
  name: 'DroneRouteList',
  props: {
    routes: {
      type: Array,
      required: true
    },
    selectedRouteId: {
      type: Number,
      default: null
    }
  },
  emits: ['route-selected'],
  data() {
    return {
      statusMap: {
        'completed': '已完成',
        'pending': '待执行',
        'in_progress': '进行中'
      }
    };
  },
  methods: {
    // 格式化日期
    formatDate(dateString) {
      if (!dateString) return '';
      const date = new Date(dateString);
      return date.toLocaleDateString('zh-CN', {
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    },
    handleRouteSelect(key) {
      this.$emit('route-selected', Number(key));
    },
    getStatusType(status) {
      switch (status) {
        case 'completed':
          return 'success';
        case 'in_progress':
          return 'primary';
        case 'pending':
          return 'warning';
        default:
          return 'info';
      }
    }
  }
};
