          resultData = await response.json();
        } catch (e) {
          throw new Error('无法解析响应数据，可能是非JSON格式');
        }
        this.result = {
          data: resultData,
          responseTime: `${(endTime - startTime).toFixed(2)} ms`,
          status: response.status,
          statusText: response.statusText,
          headers: responseHeaders
        };

        // 添加到请求历史
        this.addToHistory({
