import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.map.js";
import alarmApi from '../api/alarmApi';
export default {
  name: 'AlarmList',
  data() {
    return {
      alarms: [],
      categories: [],
      waylines: [],
      // 存储航线数据
      loading: false,
      searchQuery: '',
      statusFilter: '',
      waylineIdFilter: '',
      currentPage: 1,
      pageSize: 10,
      totalAlarms: 0,
      showStatusDialog: false,
      showDetailDialog: false,
      // 详情弹窗显示状态
      currentAlarm: null,
      newAlarmStatus: ''
    };
  },
  watch: {
    // 监听搜索关键词变化
    searchQuery: {
      handler() {
        this.currentPage = 1; // 重置到第一页
        this.loadAlarms();
      },
      debounce: 300 // 防抖，避免频繁请求
    },
    // 监听状态筛选变化
    statusFilter() {
      this.currentPage = 1;
      this.loadAlarms();
    },
    // 监听航线ID筛选变化
    waylineIdFilter: {
      handler() {
        this.currentPage = 1;
        this.loadAlarms();
      },
      debounce: 300
    }
  },
  computed: {
    // 直接使用从后端获取的数据，不再需要前端过滤
    filteredAlarms() {
      return this.alarms;
    }
  },
  async mounted() {
    await this.loadAlarms();
    await this.loadCategories();
    await this.loadWaylines();
  },
  methods: {
    async loadAlarms() {
      this.loading = true;
      try {
        // 构建请求参数，包含分页和筛选条件
        const params = {
          page: this.currentPage,
          page_size: this.pageSize,
          search: this.searchQuery,
          // 搜索关键词
          status: this.statusFilter,
          // 状态筛选
          wayline_id: this.waylineIdFilter // 航线ID筛选(后端过滤器使用的参数名)
        };
        const response = await alarmApi.getAlarms(params);

        // 转换数据格式，确保前端显示正确
        this.alarms = (response?.results || []).map(alarm => ({
          id: alarm.id,
          created_at: alarm.created_at,
          category_name: alarm.category_details ? alarm.category_details.name : alarm.category ? alarm.category.name : '未分类',
          category_id: alarm.category_details ? alarm.category_details.id : alarm.category ? alarm.category.id : null,
          latitude: alarm.latitude,
          longitude: alarm.longitude,
          content: alarm.content,
          image_url: alarm.image_url,
          status: alarm.status,
          // 支持新的外键关系结构，同时保持兼容旧格式
          wayline_id: alarm.wayline?.wayline_id || alarm.wayline_id || null,
          wayline_name: alarm.wayline?.name || null,
          // 保留完整的wayline对象
          wayline: alarm.wayline || null,
          // 保留category_details以便后续使用
          category_details: alarm.category_details || null
        }));
        this.totalAlarms = response?.count || this.alarms.length;
      } catch (error) {
        this.$message.error('获取告警列表失败，显示模拟数据');
        console.error('Load alarms error:', error);

        // 使用模拟数据，确保页面功能正常
        let mockAlarms = [{
          id: 1,
          created_at: new Date().toISOString(),
          category_name: '设备故障',
          category_id: 1,
          latitude: 39.9042,
          longitude: 116.4074,
          content: '无人机电机过热，温度达到98°C，建议立即降落检查。电机表面出现异常颜色变化，可能存在严重故障风险。',
          image_url: 'https://picsum.photos/800/600?random=1',
          status: 'PENDING',
          wayline_id: 'WL001'
        }, {
          id: 2,
          created_at: new Date(Date.now() - 3600000).toISOString(),
          category_name: '信号丢失',
          category_id: 2,
          latitude: 39.9142,
          longitude: 116.4174,
          content: '遥控信号弱，信号强度低于30%，无人机可能失控。请立即调整操作位置，确保视线内飞行。',
          image_url: 'https://picsum.photos/800/600?random=2',
          status: 'RESOLVED',
          wayline_id: 'WL002'
        }, {
          id: 3,
          created_at: new Date(Date.now() - 7200000).toISOString(),
          category_name: '电量警告',
          category_id: 3,
          latitude: 39.9242,
          longitude: 116.4274,
          content: '电池电量低于20%，请尽快返航降落。预计剩余飞行时间不超过5分钟，请注意安全。',
          image_url: 'https://picsum.photos/800/600?random=3',
          status: 'COMPLETED',
          wayline_id: 'WL001'
        }, {
          id: 4,
          created_at: new Date(Date.now() - 10800000).toISOString(),
          category_name: '天气异常',
          category_id: 4,
          latitude: 39.9342,
          longitude: 116.4374,
          content: '检测到强风，风速超过20m/s，可能影响飞行稳定性。建议立即降落至安全区域，避免发生意外。',
          image_url: null,
          status: 'IGNORED',
          wayline_id: 'WL003'
        }];

        // 当API调用失败时，在前端进行模拟过滤，确保筛选功能可见
        if (this.searchQuery) {
          const query = this.searchQuery.toLowerCase();
          mockAlarms = mockAlarms.filter(alarm => alarm.content.toLowerCase().includes(query) || alarm.category_name.toLowerCase().includes(query) || alarm.wayline_id && alarm.wayline_id.toLowerCase().includes(query));
        }
        if (this.statusFilter) {
          mockAlarms = mockAlarms.filter(alarm => alarm.status === this.statusFilter);
        }
        if (this.waylineIdFilter) {
          // 适配下拉选择框的航线ID筛选
          mockAlarms = mockAlarms.filter(alarm => alarm.wayline && alarm.wayline.wayline_id === this.waylineIdFilter || alarm.wayline_id === this.waylineIdFilter);
        }

        // 模拟分页
        const start = (this.currentPage - 1) * this.pageSize;
        const end = start + this.pageSize;
        this.alarms = mockAlarms.slice(start, end);
        this.totalAlarms = mockAlarms.length;
      } finally {
        this.loading = false;
      }
    },
    async loadCategories() {
      try {
        const categories = await alarmApi.getAlarmCategories();
        this.categories = categories?.results || categories || [];
      } catch (error) {
        this.$message.error('获取告警类型失败，使用默认类型');
        console.error('Load categories error:', error);

        // 使用模拟数据
        this.categories = [{
          id: 1,
          name: '设备故障',
          code: 'EQUIPMENT_FAILURE'
        }, {
          id: 2,
          name: '信号丢失',
          code: 'SIGNAL_LOSS'
        }, {
          id: 3,
          name: '电量警告',
          code: 'BATTERY_WARNING'
        }, {
          id: 4,
          name: '天气异常',
          code: 'WEATHER_ABNORMAL'
        }];
      }
    },
    async loadWaylines() {
      try {
        // 假设alarmApi中有获取航线列表的方法
        const response = await alarmApi.getWaylines();
        this.waylines = response?.results || response || [];
      } catch (error) {
        this.$message.error('获取航线列表失败');
        console.error('Load waylines error:', error);

        // 使用模拟数据
        this.waylines = [{
          id: 1,
          wayline_id: 'WL001',
          name: '测试航线1'
        }, {
          id: 2,
          wayline_id: 'WL002',
          name: '测试航线2'
        }, {
          id: 3,
          wayline_id: 'WL003',
          name: '河道巡查航线'
        }, {
          id: 4,
          wayline_id: 'WL004',
          name: '农林监测航线'
        }, {
          id: 5,
          wayline_id: 'WL005',
          name: '矿区测绘航线'
        }];
      }
    },
    formatDate(dateString) {
      if (!dateString) return '';
      const date = new Date(dateString);
      return date.toLocaleString('zh-CN', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      });
    },
    getStatusText(status) {
      const statusMap = {
        'PENDING': '待处理',
        'PROCESSING': '处理中',
        'COMPLETED': '已完成',
        'IGNORED': '已忽略'
      };
      return statusMap[status] || status;
    },
    getStatusType(status) {
      const typeMap = {
        'PENDING': 'danger',
        'PROCESSING': 'warning',
        'COMPLETED': 'success',
        'IGNORED': 'info'
      };
      return typeMap[status] || 'default';
    },
    getCategoryType(categoryName) {
      // 确保categoryName是字符串
      if (!categoryName || typeof categoryName !== 'string') {
        return 'info';
      }

      // 根据不同的告警类型返回不同的标签类型
      const typeKeywords = {
        '发热': 'danger',
        '损伤': 'warning',
        '故障': 'danger',
        '异常': 'warning',
        '隐患': 'warning'
      };
      for (const [keyword, type] of Object.entries(typeKeywords)) {
        if (categoryName.includes(keyword)) {
          return type;
        }
      }
      return 'info';
    },
    // 获取航线ID，支持新旧数据结构
    getWaylineId(row) {
      // 优先使用新的外键关系结构
      if (row.wayline) {
        return row.wayline.wayline_id || '--';
      }
      // 兼容旧的wayline_id字段
      return row.wayline_id || '--';
    },
    // 获取航线名称，支持新旧数据结构
    getWaylineName(row) {
      // 优先使用新的外键关系结构
      if (row.wayline) {
        return row.wayline.name || '--';
      }
      // 兼容从后端转换的wayline_name字段
      return row.wayline_name || '--';
    },
    viewAlarmDetail(alarm) {
      // 设置当前告警并显示详情弹窗
      this.currentAlarm = {
        ...alarm
      };
      this.showDetailDialog = true;
    },
    updateAlarmStatus(alarm) {
      console.log('更新状态按钮被点击', alarm);
      // 确保currentAlarm是响应式的
      this.currentAlarm = {
        ...alarm
      };
      this.newAlarmStatus = alarm.status || 'PENDING';
      // 直接设置显示状态
      this.showStatusDialog = true;
      console.log('对话框显示状态设置为true', this.showStatusDialog);
    },
    async confirmStatusUpdate() {
      if (!this.newAlarmStatus || !this.currentAlarm?.id) {
        this.$message.error('请选择有效的状态');
        return;
      }
      try {
        // 使用patchAlarm方法只更新状态字段
        await alarmApi.patchAlarm(this.currentAlarm.id, {
          status: this.newAlarmStatus
        });
        this.$message.success('告警状态更新成功');
        this.showStatusDialog = false;
        await this.loadAlarms(); // 重新加载列表
      } catch (error) {
        this.$message.error('状态更新失败');
        console.error('Update alarm status error:', error);
      }
    },
    async deleteAlarm(alarmId) {
      this.$confirm('确定要删除这条告警吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await alarmApi.deleteAlarm(alarmId);
          this.$message.success('删除成功');
          await this.loadAlarms(); // 重新加载列表
        } catch (error) {
          this.$message.error('删除失败');
          console.error('Delete alarm error:', error);
        }
      }).catch(() => {
        // 取消删除
      });
    },
    handleSizeChange(size) {
      this.pageSize = size;
      this.loadAlarms();
    },
    handleCurrentChange(current) {
      this.currentPage = current;
      this.loadAlarms();
    }
  }
};
