export default {
  name: 'MonitorPanel',
  props: {
    videoStreamUrl: {
      type: String,
      default: ''
    },
    droneStatus: {
      type: String,
      default: '正常'
    },
    batteryLevel: {
      type: Number,
      default: 80
    },
    altitude: {
      type: Number,
      default: 0
    },
    speed: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      videoUrl: ''
    };
  },
  mounted() {
    this.videoUrl = this.videoStreamUrl || 'https://picsum.photos/800/450';
  },
  methods: {
    handleRefresh() {
      this.videoUrl = '';
      setTimeout(() => {
        this.videoUrl = this.videoStreamUrl || 'https://picsum.photos/800/450?' + Math.random();
      }, 300);
    }
  }
};
