import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import store from './store'
import ElementPlus from 'element-plus'
import 'element-plus/dist/index.css'
import './assets/tailwind.css'
import 'font-awesome/css/font-awesome.min.css'

// 处理ResizeObserver循环错误
if (typeof window !== 'undefined') {
  window.ResizeObserver = new Proxy(window.ResizeObserver || {}, {
    construct: (target, args) => {
      const callback = args[0]
      return new target((entries, observer) => {
        window.requestAnimationFrame(() => {
          callback(entries, observer)
        })
      }, args[1])
    }
  })

  // 全局暗黑金属风格样式覆盖
  const globalStyles = document.createElement('style')
  globalStyles.textContent = `
    /* Element Plus 下拉选择框全局样式覆盖 */
    .el-select .el-input__wrapper {
      background-color: #1f2937 !important;
      border: 1px solid #374151 !important;
      box-shadow: none !important;
    }

    .el-select .el-input__inner {
      background-color: #1f2937 !important;
      color: #d1d5db !important;
    }

    .el-select .el-input__suffix-inner .el-input__icon {
      color: #9ca3af !important;
    }

    /* 筛选框特定样式 */
    .search-filters .el-select .el-input__wrapper {
      background-color: #1f2937 !important;
      border: 1px solid #374151 !important;
    }

    .search-filters .el-select .el-input__inner {
      background-color: #1f2937 !important;
      color: #d1d5db !important;
    }

    /* 下拉菜单样式 */
    .el-select-dropdown {
      background-color: #1f2937 !important;
      border: 1px solid #374151 !important;
    }

    .el-select-dropdown__item {
      color: #d1d5db !important;
    }

    .el-select-dropdown__item:hover {
      background-color: #374151 !important;
      color: #93c5fd !important;
    }

    .el-select-dropdown__item.selected {
      background-color: #1e3a8a !important;
      color: #93c5fd !important;
    }
  `
  document.head.appendChild(globalStyles)
}

console.log('开始创建 Vue 应用')

const app = createApp(App)

console.log('Vue 应用创建完成')

// 配置Element Plus暗黑主题
const darkThemeConfig = {
  global: {
    primaryColor: '#3b82f6',
    successColor: '#10b981',
    warningColor: '#f59e0b',
    dangerColor: '#ef4444',
    infoColor: '#64748b'
  },
  button: {
    textColor: '#d1d5db',
    borderColor: '#4b5563'
  },
  input: {
    textColor: '#d1d5db',
    borderColor: '#4b5563',
    fillColor: '#1f2937'
  }
}

// 使用插件
app.use(router)
app.use(store)
app.use(ElementPlus, { theme: darkThemeConfig })

console.log('插件安装完成')

// 挂载应用
app.mount('#app')

console.log('Vue 应用挂载完成')
