import { createApp } from 'vue'

console.log('开始创建最简 Vue 应用')

const app = createApp({
  data() {
    return {
      message: '你好，Vue!'
    }
  },
  template: `
    <div id="app">
      <h1>{{ message }}</h1>
      <p>如果你能看到这个信息，说明 Vue 能正常工作。</p>
    </div>
  `
})

console.log('最简 Vue 应用创建完成')

// 挂载应用
app.mount('#app')

console.log('最简 Vue 应用挂载完成')
