import axios from 'axios'

// 创建axios实例
const api = axios.create({
  baseURL: '/api/v1',
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json'
  }
})

// 请求拦截器
api.interceptors.request.use(
  config => {
    // 可以在这里添加token等认证信息
    return config
  },
  error => {
    return Promise.reject(error)
  }
)

// 响应拦截器
api.interceptors.response.use(
  response => {
    return response.data
  },
  error => {
    console.error('API请求错误:', error)
    return Promise.reject(error)
  }
)

// 模拟数据
const mockAlarms = [
  {
    id: 1,
    category: {
      id: 1,
      name: '设备故障',
      code: 'EQUIPMENT_FAILURE',
      description: '设备运行异常'
    },
    content: '无人机电机过热',
    latitude: 39.9042,
    longitude: 116.4074,
    image_url: null,
    specific_data: { "temperature": 85, "motor_id": 2 },
    status: 'PENDING',
    handler: null,
    created_at: '2024-01-15T10:30:00Z',
    updated_at: '2024-01-15T10:30:00Z'
  },
  {
    id: 2,
    category: {
      id: 2,
      name: '信号丢失',
      code: 'SIGNAL_LOSS',
      description: '通信信号中断'
    },
    content: '遥控信号弱',
    latitude: 39.9142,
    longitude: 116.4174,
    image_url: null,
    specific_data: { "signal_strength": 25 },
    status: 'RESOLVED',
    handler: '张三',
    created_at: '2024-01-14T15:45:00Z',
    updated_at: '2024-01-14T16:00:00Z'
  },
  {
    id: 3,
    category: {
      id: 3,
      name: '电量警告',
      code: 'BATTERY_WARNING',
      description: '电池电量不足'
    },
    content: '电池电量低于20%',
    latitude: 39.8942,
    longitude: 116.3974,
    image_url: null,
    specific_data: { "battery_level": 18 },
    status: 'PENDING',
    handler: null,
    created_at: '2024-01-15T09:15:00Z',
    updated_at: '2024-01-15T09:15:00Z'
  }
]

const mockAlarmCategories = [
  { id: 1, name: '设备故障', code: 'EQUIPMENT_FAILURE', description: '设备运行异常', parent: null },
  { id: 2, name: '信号丢失', code: 'SIGNAL_LOSS', description: '通信信号中断', parent: null },
  { id: 3, name: '电量警告', code: 'BATTERY_WARNING', description: '电池电量不足', parent: null },
  { id: 4, name: '天气异常', code: 'WEATHER_ABNORMAL', description: '环境条件不适宜飞行', parent: null }
]

// 告警信息API服务
export default {
  // 获取告警列表
  async getAlarms(params = {}) {
    try {
      const response = await api.get('/alarms/', { params })
      return response
    } catch (error) {
      console.error('获取告警列表失败，使用模拟数据:', error)
      // 返回模拟数据，模拟分页结构
      return {
        count: mockAlarms.length,
        next: null,
        previous: null,
        results: mockAlarms
      }
    }
  },
  
  // 获取单个告警详情
  async getAlarmDetail(alarmId) {
    try {
      const response = await api.get(`/alarms/${alarmId}/`)
      return response
    } catch (error) {
      console.error('获取告警详情失败，使用模拟数据:', error)
      // 从模拟数据中查找
      return mockAlarms.find(alarm => alarm.id === parseInt(alarmId)) || null
    }
  },
  
  // 创建新告警
  async createAlarm(alarmData) {
    try {
      const response = await api.post('/alarms/', alarmData)
      return response
    } catch (error) {
      console.error('创建告警失败:', error)
      // 模拟创建成功
      const newAlarm = {
        id: mockAlarms.length + 1,
        ...alarmData,
        status: 'PENDING',
        handler: null,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString()
      }
      mockAlarms.unshift(newAlarm)
      return newAlarm
    }
  },
  
  // 更新告警
  async updateAlarm(alarmId, alarmData) {
    try {
      const response = await api.put(`/alarms/${alarmId}/`, alarmData)
      return response
    } catch (error) {
      console.error('更新告警失败:', error)
      // 模拟更新
      const index = mockAlarms.findIndex(alarm => alarm.id === parseInt(alarmId))
      if (index !== -1) {
        mockAlarms[index] = {
          ...mockAlarms[index],
          ...alarmData,
          updated_at: new Date().toISOString()
        }
        return mockAlarms[index]
      }
      throw new Error('告警不存在')
    }
  },
  
  // 部分更新告警（如状态）
  async patchAlarm(alarmId, alarmData) {
    try {
      const response = await api.patch(`/alarms/${alarmId}/`, alarmData)
      return response
    } catch (error) {
      console.error('部分更新告警失败:', error)
      // 模拟部分更新
      const index = mockAlarms.findIndex(alarm => alarm.id === parseInt(alarmId))
      if (index !== -1) {
        mockAlarms[index] = {
          ...mockAlarms[index],
          ...alarmData,
          updated_at: new Date().toISOString()
        }
        return mockAlarms[index]
      }
      throw new Error('告警不存在')
    }
  },
  
  // 删除告警
  async deleteAlarm(alarmId) {
    try {
      const response = await api.delete(`/alarms/${alarmId}/`)
      return response
    } catch (error) {
      console.error('删除告警失败:', error)
      // 模拟删除
      const index = mockAlarms.findIndex(alarm => alarm.id === parseInt(alarmId))
      if (index !== -1) {
        mockAlarms.splice(index, 1)
        return { success: true }
      }
      throw new Error('告警不存在')
    }
  },
  
  // 获取告警类型列表
  async getAlarmCategories() {
    try {
      const response = await api.get('/alarm-categories/')
      return response
    } catch (error) {
      console.error('获取告警类型列表失败，使用模拟数据:', error)
      // 返回模拟数据
      return {
        count: mockAlarmCategories.length,
        next: null,
        previous: null,
        results: mockAlarmCategories
      }
    }
  }
}
